import React, {useState} from "react";
const Login = () => {
    const [company_id, setCompanyid] = useState("");
    const [password, setPassword] = useState("");
    const [allEntry, setAllEntry] = useState([]);
    const submitForm = (e) => {
        e.preventDefault();
        const newEntry = {company_id:company_id, password:password}
        setAllEntry([...allEntry, newEntry]);
    }
return (
<>
<form action="" onSubmit={submitForm}>
    <div classname = "Login_Layout">
    <h1> <b> Welcome to Login Page  </b></h1>
    <h1> Enter Your Credentials </h1>
    <label> <h2> Company Id </h2> <br/>
    <input type="numbers" id="company_id" name="company_id" autoComplete="off" 
     value={company_id} onChange={(e) => setCompanyid(e.target.value)} required placeholder ="123456789"       />
    </label> <br/>
    </div>
    <div>
    <label> <h2> Password </h2> <br/>
    <input type="password" id="password" name="password" autoComplete="off" 
    value={password} onChange={(e) => setPassword(e.target.value)}/> 
    </label>
    </div>
    <br/>
    <button type = "Login"> Login </button>
    </form>
    </>
    );
}
export default Login;
